// Node-RED Function Node: Advanced Maintenance for Dynamic ESS Schedule
// Supports multiple maintenance actions via msg.action

const action = msg.action || msg.payload || 'clear'; // Default to 'clear' if not specified
const currentTime = Math.floor(Date.now() / 1000);

// Get existing slots
const existingSlots = global.get('dynamicEssScheduleSlots') || {};
const slotCount = Object.keys(existingSlots).length;

let result = {};
let expiredCount = 0;

switch (action) {
    case 'clear':
    case 'clear_all':
        // Clear all cached slots
        global.set('dynamicEssScheduleSlots', {});
        node.status({ fill: 'green', shape: 'ring', text: `Cleared ${slotCount} slots` });
        node.warn(`Maintenance: Cleared all ${slotCount} cached schedule slots`);

        result = {
            action: 'clear_all',
            clearedSlots: slotCount,
            timestamp: new Date().toISOString()
        };
        break;

    case 'clear_expired':
        // Remove only expired slots
        const cleaned = {};

        for (const [slotNum, slotData] of Object.entries(existingSlots)) {
            const endTime = slotData.Start + slotData.Duration;
            if (endTime >= currentTime) {
                // Keep non-expired
                cleaned[slotNum] = slotData;
            } else {
                expiredCount++;
            }
        }

        global.set('dynamicEssScheduleSlots', cleaned);
        node.status({ fill: 'blue', shape: 'ring', text: `Removed ${expiredCount} expired` });
        node.warn(`Maintenance: Removed ${expiredCount} expired slots, kept ${Object.keys(cleaned).length}`);

        result = {
            action: 'clear_expired',
            removedSlots: expiredCount,
            remainingSlots: Object.keys(cleaned).length,
            timestamp: new Date().toISOString()
        };
        break;

    case 'info':
    case 'status':
        // Show current status without clearing
        let activeCount = 0;

        for (const [slotNum, slotData] of Object.entries(existingSlots)) {
            const endTime = slotData.Start + slotData.Duration;
            if (endTime >= currentTime) {
                activeCount++;
            } else {
                expiredCount++;
            }
        }

        node.status({ fill: 'yellow', shape: 'dot', text: `${activeCount} active, ${expiredCount} expired` });

        result = {
            action: 'info',
            totalSlots: slotCount,
            activeSlots: activeCount,
            expiredSlots: expiredCount,
            availableSlots: 48 - activeCount,
            timestamp: new Date().toISOString()
        };
        break;

    case 'dump':
    case 'export':
        // Export all slots for inspection
        node.status({ fill: 'grey', shape: 'dot', text: `Exported ${slotCount} slots` });

        result = {
            action: 'export',
            slots: existingSlots,
            totalSlots: slotCount,
            timestamp: new Date().toISOString()
        };
        break;

    default:
        node.status({ fill: 'red', shape: 'ring', text: 'Unknown action' });
        node.error(`Unknown maintenance action: ${action}. Use: clear, clear_expired, info, or dump`);

        result = {
            error: 'Unknown action',
            action: action,
            validActions: ['clear', 'clear_expired', 'info', 'dump']
        };
}

msg.payload = result;
return msg;

// Node-RED Function Node: Process Dynamic ESS Schedule
// Place this code in a Function node after your VRM API fetch node

const MAX_SLOTS = 4;
const PROPERTIES = ['Duration', 'Start', 'AllowGridFeedin', 'Soc', 'Restrictions', 'Flags', 'Strategy'];
const allowNonEqualArrays = false; // Set to false to enforce strict array length validation

// Get current time (use msg.timestamp for testing, otherwise Date.now())
const currentTime = msg.timestamp || Math.floor(Date.now() / 1000);

// Extract schedule from payload
if (!msg.payload || !msg.payload.schedule) {
    node.status({ fill: 'red', shape: 'ring', text: 'Invalid input' });
    node.error('Invalid message: msg.payload.schedule is required');
    return null;
}

const schedule = msg.payload.schedule;

// Validate schedule structure
try {
    validateSchedule(schedule, PROPERTIES, allowNonEqualArrays);
} catch (error) {
    node.status({ fill: 'red', shape: 'ring', text: error.message });
    node.error(error.message);
    return null;
}

// Get existing slots from global context
const existingSlots = global.get('dynamicEssScheduleSlots') || {};

// Identify expired slots
const expiredSlotNumbers = findExpiredSlots(existingSlots, currentTime);

// Build new slot assignments and collect changes
const { newSlots, changes, stats } = processScheduleEntries(
    schedule,
    existingSlots,
    expiredSlotNumbers,
    currentTime,
    MAX_SLOTS,
    PROPERTIES
);

// Save updated slots to context
global.set('dynamicEssScheduleSlots', newSlots);

// Update node status
updateNodeStatus(node, stats, changes.length);

// Return array of change messages
return [changes];

// ===== Helper Functions =====

function validateSchedule(schedule, properties, allowNonEqual) {
    // Check all required properties exist
    for (const prop of properties) {
        if (!Array.isArray(schedule[prop])) {
            throw new Error(`Schedule property '${prop}' must be an array`);
        }
    }

    // Check all arrays have same length
    const lengths = properties.map(prop => schedule[prop].length);
    const minLength = Math.min(...lengths);
    const maxLength = Math.max(...lengths);

    if (minLength !== maxLength) {
        if (allowNonEqual) {
            // Truncate all arrays to minimum length
            node.warn(`Array length mismatch detected. Truncating to ${minLength} entries (shortest array).`);
            for (const prop of properties) {
                if (schedule[prop].length > minLength) {
                    schedule[prop].length = minLength;
                }
            }
        } else {
            throw new Error('All schedule arrays must have the same length');
        }
    }
}

function findExpiredSlots(slots, currentTime) {
    const expired = [];

    for (const [slotNum, slotData] of Object.entries(slots)) {
        const endTime = slotData.Start + slotData.Duration;
        if (endTime < currentTime) {
            expired.push(parseInt(slotNum));
        }
    }

    return expired.sort((a, b) => a - b);
}

function processScheduleEntries(schedule, existingSlots, expiredSlotNumbers, currentTime, maxSlots, properties) {
    const newSlots = { ...existingSlots };
    const changes = [];
    const scheduleLength = schedule.Start.length;

    // Statistics for status reporting
    const stats = {
        totalEntries: scheduleLength,
        expiredEntries: 0,
        processedEntries: 0,
        newSlots: 0,
        updatedSlots: 0,
        unchangedSlots: 0,
        discardedOverLimit: 0
    };

    // Create a map of Start time to existing slot number
    const startTimeToSlot = {};
    for (const [slotNum, slotData] of Object.entries(existingSlots)) {
        startTimeToSlot[slotData.Start] = parseInt(slotNum);
    }

    // Track which slots we've assigned
    const usedSlots = new Set(Object.keys(existingSlots).map(n => parseInt(n)));

    // Process each schedule entry and filter expired ones
    const scheduleEntries = [];
    for (let i = 0; i < scheduleLength; i++) {
        const entry = {
            Duration: schedule.Duration[i],
            Start: schedule.Start[i],
            AllowGridFeedin: schedule.AllowGridFeedin[i],
            Soc: schedule.Soc[i],
            Restrictions: schedule.Restrictions[i],
            Flags: schedule.Flags[i],
            Strategy: schedule.Strategy[i]
        };

        // Skip expired entries
        const endTime = entry.Start + entry.Duration;
        if (endTime < currentTime) {
            stats.expiredEntries++;
            continue;
        }

        scheduleEntries.push(entry);
    }

    // If we have more than maxSlots non-expired entries, keep only first maxSlots
    // Sort by Start time and limit to maxSlots
    if (scheduleEntries.length > maxSlots) {
        scheduleEntries.sort((a, b) => a.Start - b.Start);
        stats.discardedOverLimit = scheduleEntries.length - maxSlots;
        scheduleEntries.length = maxSlots;
    }

    // Track available expired slots for reuse
    let availableExpiredSlots = [...expiredSlotNumbers];

    // Process each schedule entry
    for (const entry of scheduleEntries) {
        let slotNum;
        let hasChanges = false;

        // Check if this Start time already exists in a non-expired slot
        if (startTimeToSlot[entry.Start] !== undefined) {
            slotNum = startTimeToSlot[entry.Start];

            // Compare and collect changes
            const existingEntry = existingSlots[slotNum];
            for (const prop of properties) {
                if (existingEntry[prop] !== entry[prop]) {
                    changes.push({
                        path: `/Settings/DynamicEss/Schedule/${slotNum}/${prop}`,
                        payload: entry[prop]
                    });
                    hasChanges = true;
                }
            }

            if (hasChanges) {
                stats.updatedSlots++;
            } else {
                stats.unchangedSlots++;
            }

            // Update slot data
            newSlots[slotNum] = entry;
        } else {
            // New entry - find a slot for it

            // First, try to reuse an expired slot
            if (availableExpiredSlots.length > 0) {
                slotNum = availableExpiredSlots.shift();
            } else {
                // No expired slots, find next available slot number
                slotNum = 0;
                while (usedSlots.has(slotNum) && slotNum < maxSlots) {
                    slotNum++;
                }

                if (slotNum >= maxSlots) {
                    // All slots full - this should not happen since we limited to maxSlots above
                    node.warn(`Unexpected: Could not assign slot for entry with Start=${entry.Start}`);
                    continue;
                }
            }

            // Write all properties for new slot
            for (const prop of properties) {
                changes.push({
                    path: `/Settings/DynamicEss/Schedule/${slotNum}/${prop}`,
                    payload: entry[prop]
                });
            }

            stats.newSlots++;

            // Update tracking
            newSlots[slotNum] = entry;
            usedSlots.add(slotNum);
            startTimeToSlot[entry.Start] = slotNum;
        }

        stats.processedEntries++;
    }

    return { newSlots, changes, stats };
}

function updateNodeStatus(node, stats, changeCount) {
    if (changeCount === 0) {
        node.status({ 
            fill: 'green', 
            shape: 'dot', 
            text: `No changes (${stats.processedEntries} slots)` 
        });
    } else if (stats.newSlots > 0) {
        node.status({ 
            fill: 'blue', 
            shape: 'dot', 
            text: `${changeCount} changes (${stats.newSlots} new, ${stats.updatedSlots} updated)` 
        });
    } else {
        node.status({ 
            fill: 'yellow', 
            shape: 'dot', 
            text: `${changeCount} changes (${stats.updatedSlots} updated)` 
        });
    }

    // Build warning message
    const discardedParts = [];
    if (stats.expiredEntries > 0) {
        discardedParts.push(`${stats.expiredEntries} expired`);
    }
    if (stats.discardedOverLimit > 0) {
        discardedParts.push(`${stats.discardedOverLimit} over 48 limit`);
    }

    if (discardedParts.length > 0) {
        const totalDiscarded = stats.expiredEntries + stats.discardedOverLimit;
        node.warn(`Ignored ${totalDiscarded} of ${stats.totalEntries} entries: ${discardedParts.join(', ')}`);
    }
}
